mapper.insert:


					select seq_dk_chat_room.nextval from dual


 			insert into dk_chat_room (	  chatRoomNum
							 			, roomOwnerId
							 			, roomOwnerNick
							 			, chat_type
					 			   )
					 		values(
						 				 #{chatRoomNum}
										,#{roomOwnerId}
										,#{roomOwnerNick}
										,#{chat_type}				 		
					 				)

 			insert into dk_chat_member (	  chatRoomNum
								 			, chat_memberId
								 			, chat_memberNick
						 			   )
						 		values(
							 				 #{chatRoomNum}
											,#{chat_memberId}
											,#{chat_memberNick}
						 				)

mapper.select:
select /*+INDEX_ASC(dk_chat_content pk_chat_content) */ chat_writerId , chat_writerNick , chat_content , content_type , regdate from dk_chat_content where chatRoomNum = #{chatRoomNum}
and regdate > #{recentOutDate}
and chatContentNum > 0

mapper.insert:
 			insert into dk_chat_content (	  chatContentNum
								 			, chatRoomNum
								 			, chat_content
								 			, chat_writerId
								 			, chat_writerNick
								 			, regDate
						 			   )
						 		values(
							 				 seq_dk_chat_content.nextval
											,#{chatRoomNum}
											,#{chat_content}
											,#{chat_writerId}
											,#{chat_writerNick}
											,#{regDate}
						 				)

 			insert into dk_chat_content (	  chatContentNum
								 			, chatRoomNum
								 			, chat_content
	 										, content_type
						 			   )
						 		values(
							 				 seq_dk_chat_content.nextval
											,#{chatRoomNum}
											,#{chat_content}
											, 1
						 				)
