mapper.insert:
insert into DK_REPLY (   
							  reply_num
							, board_num
							, reply_content
							, nickName
							, userId
							, group_num
							, order_step
							, depth
					 )
				values (  
							  seq_dk_reply.nextval
							, #{board_num}
							, #{reply_content}
							, #{nickName}
							, #{userId}
							, seq_dk_reply.currval
							, 0
							, 0
					   )

insert into DK_REPLY (    
						  reply_num
						, board_num
						, reply_content
						, nickName
						, userId
						, group_num
						, order_step
						, depth
						, toUserId
						, toNickName
					 )
			values (   
						seq_dk_reply.nextval
					 , #{board_num}
					 , #{reply_content}
					 , #{nickName}
					 , #{userId}
					 , #{group_num}
					 , #{order_step}
					 , #{depth}+1
					 , #{toUserId}
					 , #{toNickName} 
				   )

insert into dk_reply_like (	    userId
							  , reply_num
							  , likeValue
						  )
					values (   #{userId}
							  ,#{reply_num}
							  ,'push'
							)

insert into dk_reply_dislike (	    userId
								  , reply_num
								  , dislikeValue
							  )
						values (   #{userId}
								  ,#{reply_num}
								  ,'push'
								)
